import React, { Component } from 'react';
import Input from '../common/Input';

class LoginForm extends Component {
    state = { 
        account: {
            username: '',
            password: ''
        }
     }
    handleSubmit = (e) => {
        e.preventDefault();
        console.log(e);
    }

    handleChange = (e) => {
        let account = {...this.state.account}
        account[e.currentTarget.name] = e.currentTarget.value;
        this.setState({
            account: account
        })
    }

    render() { 
        return ( 
            <div className="row">
                <div className="col-md-4"></div>
                <div className="col-md-4">
                    <h3>Login</h3>
                    <form onSubmit={this.handleSubmit}>
                        <Input label={ Username } id='username' name='username' onChange={ this.handleChange } value={ this.state.account.username } />

                        <div className="form-group">
                            <label htmlFor="password" className="label">Password</label>
                            <input id="password" type="password" className="form-control" name="password" onChange={ this.handleChange } value={ this.state.account.password }/>
                        </div>

                        <button className="btn btn-primary btn-block">Login</button>
                    </form>
                </div> 
            </div>
         );
    }
}

export default LoginForm;
