import React, { Component } from 'react';
import Like from '../common/Like';

class MoviesTable extends Component {
    sortMovies = (path) => {
        //for handling reverse of the ordering first duplicate the object
        //check what the previous path is
        const sortColumn = {...this.props.sortColumn};
        if(sortColumn.path === path){
            sortColumn.order = (sortColumn.order === 'asc') ? 'desc' : 'asc';
        }else{
            sortColumn.path = path;
            sortColumn.order = 'asc';
        }

        this.props.onSort(sortColumn);
    }
    render() { 
        let { movies, onDelete, onLike, onSort } = this.props
        return ( 
            <table className="table">
                <thead>
                    <tr>
                        <th onClick={ () => this.sortMovies('title')}>Title</th>
                        <th onClick={ () => this.sortMovies('genre')}>Genre</th>
                        <th onClick={ () => this.sortMovies('numberInStock')}>No. in stock</th>
                        <th></th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    { movies.map( movie => (<tr key={ movie._id}>
                                    <td>{ movie.title }</td>
                                    <td>{ movie.genre.name }</td>
                                    <td>{ movie.numberInStock }</td>
                                    <td>
                                        <Like movie={ movie } movieLiked={ () => onLike(movie) } />
                                    </td>
                                    <td><button className="btn btn-danger btn-xs" onClick={() => onDelete(movie._id) }>Delete</button></td>
                                </tr>))
                    }
                </tbody>
            </table>
         );
    }
}

export default MoviesTable;
