import React, { Component } from 'react';
import Like from '../common/Like';
import TableHeader from '../common/TableHeader';
import TableBody from '../common/TableBody';

class MoviesTable extends Component {
    columns = [
        {path: 'title', label: 'Title'},
        {path: 'genre.name', label:'Genre'},
        {path: 'numberInStock', label:"Number in stock"},
       // {key: 'like', content: (movie) => <Like movie={movie} onClick={ () => this.props.onLike(movie)}/>},
       // {key: 'action', content: (movie) => <button className="btn btn-xs btn-danger" onClick={ this.props.onDelete(movie._id)}>Delete</button>}
       { key: 'like', content: (movie) => console.log(movie)}
    ]
    render() { 
        let { movies, onDelete, onLike, onSort, sortColumn } = this.props
        return ( 
            <table className="table">
                <TableHeader 
                    columns={ this.columns } 
                    onSort={ onSort } 
                    sortColumn={ sortColumn }
                />

                <TableBody
                    columns={ this.columns }
                    data={ movies }
                />
                {/* <tbody>
                    { movies.map( movie => (<tr key={ movie._id}>
