import React, { Component } from 'react';
import Input from '../common/Input';
import Joi from 'joi';

class LoginForm extends Component {
    state = { 
        account: {
            username: '',
            password: ''
        },
        errors: {}
     }

    //declaring schema for validation
    schema = {
        username: Joi.string().required(),
        password: Joi.string().required()
    }

    validate = () => {
        const result = Joi.validate(this.state.account, this.schema, { abortEarly: false });
        console.log(result.error);
        //check if no error exists then return null
        //if(!error) return null;

        //create a new errors object for storing errors
        // const errors = {};
        // for(let item in error.details) errors[item.path[0]] = item.message;
        //  return errors;
    }

    handleSubmit = (e) => {
        e.preventDefault();
        let errors = this.validate();
        // if(!errors){
        //     this.setState({
        //         errors: errors
        //     });
        //     console.log(this.state.errors);
        // }else{
        //     console.log('submitted');
        // }

    }

    handleChange = (e) => {
        let account = {...this.state.account}

        account[e.currentTarget.name] = e.currentTarget.value;
        this.setState({
            account: account
        })
    }

    render() { 
        return ( 
            <div className="row">
                <div className="col-md-4"></div>
                <div className="col-md-4">
                    <h3>Login</h3>
                    <form onSubmit={this.handleSubmit}>
                        <Input label='Username' id='username' name='username' onChange={ this.handleChange } value={ this.state.account.username } />

                        <Input label="Password" id="password" type="password" name="password" onChange={ this.handleChange } value={ this.state.account.password } />

                        <button className="btn btn-primary btn-block" style={{ marginTop: 10}}>Login</button>
                    </form>
                </div> 
            </div>
         );
    }
}

export default LoginForm;
