import React, { Component } from 'react';
import PropTypes from 'prop-types';

class ListGroup extends Component {
    render() { 
        let { genres:lists, valueProperty, textProperty, onGenreChange } = this.props;
        return ( 
            <ul className="list-group">
                { lists.map( list => <li className="list-group-item" key={ list[valueProperty] } onClick={ () => onGenreChange(list) } >{ list[textProperty] }</li>)}
            </ul> 
        );
    }
}

//validating the props
ListGroup.propTypes = {
    genres: PropTypes.array.isRequired,
    onGenreChange: PropTypes.func.isRequired
}

export default ListGroup;
