import React, { Component } from 'react';
import Like from '../common/Like';

class MoviesTable extends Component {
    state = {  }
    render() { 
        let { movies, onDelete, onLike, onSort } = this.props
        return ( 
            <table className="table">
                <thead>
                    <tr>
                        <th onClick={ () => onSort('title')}>Title</th>
                        <th onClick={ () => onSort('genre')}>Genre</th>
                        <th onClick={ () => onSort('numberOfStock')}>No. in stock</th>
                        <th></th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    { movies.map( movie => (<tr key={ movie._id}>
                                    <td>{ movie._id }</td>
                                    <td>{ movie.title }</td>
                                    <td>{ movie.genre.name }</td>
                                    <td>{ movie.numberInStock }</td>
                                    <td>
                                        <Like movie={ movie } movieLiked={ () => onLike(movie) } />
                                    </td>
                                    <td><button className="btn btn-danger btn-xs" onClick={() => onDelete(movie._id) }>Delete</button></td>
                                </tr>))
                    }
                </tbody>
            </table>
         );
    }
}

export default MoviesTable;
