import React, { Component } from 'react';
import { getMovies } from "../services/fakeMovieService";
import { getGenres } from '../services/fakeGenreService';
import Like from "../common/Like"
import Pagination from "../common/Pagination"
import {Paginate} from '../helpers/Paginate'
import ListGroup from '../common/ListGroup';

class Movies extends Component {
    state = { 
        movies: [],
        genres: [],
        pageSize: 4,
        selectedGenre: '',
        currentPage: 1
     }

    componentDidMount(){
        const genres = [{name: 'All Genre'}, ...getGenres()]
        this.setState({
            movies: getMovies(),
            genres: genres
        });
    } 

    handleDelete = (id) => {
        let movies = this.state.movies.filter( movie => movie._id != id)
        this.setState({
            movies: movies
        });
    }

    handleLikeMovie = (movie) => {
        let movies = [...this.state.movies];
        let new_movie = {...movie};
        const index = movies.indexOf(movie);
        movies[index] = new_movie;
        movies[index].like = !(movies[index].like);
        this.setState({
            movies: movies
        })
    }

    handlePageChange = (page) => {
        this.setState({
            currentPage: page
        })
    }

    handleGenreChange = (genre) => {
        this.setState({
            selectedGenre: genre,
            currentPage: 1
        })
    }

    render() { 
        //some object destructuring
        let {length: count} = this.state.movies
        let {pageSize, currentPage, movies:allMovies, selectedGenre} = this.state
        //if any genre selected then filter the movie according to that genre
        let filteredMovies = selectedGenre && selectedGenre._id 
            ? allMovies.filter( m => m.genre._id === selectedGenre._id ) 
            : allMovies;

        const movies = Paginate(filteredMovies, currentPage, pageSize);

        if(count === 0)
            return <p style={{fontWeight: 'bold', textAlign: 'center'}}>No movie found</p>    
        return (     
        <main role="main" className="container" style={{padding: 23}}>
            <div className="row">
                <div className="col-2">
                    <ListGroup 
                        genres={ this.state.genres } 
                        selectedGenre={ this.state.selectedGenre } 
                        onGenreChange={ this.handleGenreChange }
                    />
                </div>

                <div className="col">
                    <p style={{fontWeight: 'bold', textAlign: 'center'}}>{ filteredMovies.length } movies found </p>
                    <div className="starter-template">
                        <table className="table">
                            <thead>
                                <tr>
                                    <th>Id</th>
                                    <th>Title</th>
                                    <th>No. in stock</th>
                                    <th></th>
                                    <th>Action</th>
                                </tr>
                            </thead>
                            <tbody>
                                { movies.map( movie => (<tr key={ movie._id}>
                                                <td>{ movie._id }</td>
                                                <td>{ movie.title }</td>
                                                <td>{ movie.numberInStock }</td>
                                                <td>
                                                    <Like movie={ movie } movieLiked={ this.handleLikeMovie} />
                                                </td>
                                                <td><button className="btn btn-danger btn-xs" onClick={() => this.handleDelete(movie._id)}>Delete</button></td>
                                            </tr>))
                                }
                            </tbody>
                        </table>

                        <Pagination 
                            itemsCount={ filteredMovies.length } 
                            pageSize={ pageSize } 
                            currentPage={ currentPage } 
                            onPageChange={ this.handlePageChange }
                        />
                    </div>
                </div>
            </div>

        </main>
      );
    }
}

export default Movies;
