import React, { Component } from 'react';
import Like from '../common/Like';
import TableHeader from '../common/TableHeader';

class MoviesTable extends Component {
    columns = [
        {path: 'title', label: 'Title'},
        {path: 'genre.name', label:'Genre'},
        {path: 'numberInStock', label:"Number in stock"},
        {key: 'like'},
        {key: 'action'}
    ]
    render() { 
        let { movies, onDelete, onLike, onSort, sortColumn } = this.props
        return ( 
            <table className="table">
                <TableHeader 
                    columns={ this.columns } 
                    onSort={ onSort } 
                    sortColumn={ sortColumn }
                />

                <tbody>
                    { movies.map( movie => (<tr key={ movie._id}>
                                    <td>{ movie.title }</td>
                                    <td>{ movie.genre.name }</td>
                                    <td>{ movie.numberInStock }</td>
                                    <td>
                                        <Like movie={ movie } movieLiked={ () => onLike(movie) } />
                                    </td>
                                    <td><button className="btn btn-danger btn-xs" onClick={() => onDelete(movie._id) }>Delete</button></td>
                                </tr>))
                    }
                </tbody>
            </table>
         );
    }
}

export default MoviesTable;
